import React, {Component} from 'react';
import {MY_EMAIL} from "../config/configuration";
import * as emailjs from 'emailjs-com'

class Contact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            text: ''
        }
    }

    handleChange = (e) => {
        e.preventDefault();
        let email = e.target.value;
        this.setState({
            email
        })
    };

    handleChangeTextArea = (e) => {
        e.preventDefault();
        let text = e.target.value;
        this.setState({
            text
        })
    };

    handleSubmit = () => {
        var templateParams = {
            to_name: 'xyz',
            from_name: 'abc',
            message_html: 'Please Find out the attached file'
        };

        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams)
            .then(function(response) {
                console.log('SUCCESS!', response.status, response.text);
            }, function(error) {
                console.log('FAILED...', error);
            });

        this.resetForm()
    };


    resetForm = () => {
        this.setState({
            email: '',
            subject: '',
            text: '',
        })
    };



    render() {
        return (
            <div className={'container-fluid contact'}>
                <div className={'container'}>

                    <div className={'contact-text p-5'}>
                        <h2>
                            Hey dear!
                        </h2>
                        <h3>
                            If you have a question to me, you can send me an email right here!
                        </h3>
                    </div>

                    <div className={'container forms row'}>
                        <input onChange={this.handleChange}
                               type={'email'} placeholder={'Email'}
                               className={'col-md-8 offset-2 text-center'}/>
                        <br/>
                        <textarea onChange={this.handleChangeTextArea}
                                  className={"col-md-8 offset-2 mt-3"}/>
                        <br/>
                        <button onClick={this.handleSubmit} className={'col-md-2 offset-5 mt-3 button'}>Send</button>
                    </div>

                </div>
            </div>
        );
    }
}

